Type== "t"){          $data['result'] = $this->repository_model->searchByTag($q);          $this->load->view('results', $data);      }else if($keywordType == "ti"){          $data['result'] = $this->repository_model->searchByPaper($q);          $this->load->view('results', $data);      }else if($keywordType == "n"){          $data['result'] = $this->repository_model->searchByName($q);          $this->load->view('results', $data);      }else{          $data['result'] = $this->repository_model->searchByPaper($q);          $this->load->view('results', $data);      }  //if($keywordResult[0]== 't') {  //}  }    /* * Email user with $link * required $user_name,$email_id,$link **/    public function email_user($name, $link, $email_id){        $this->load->library('email');        $config['protocol'] = "smtp";        $config['smtp_host'] = "tls://smtp.googlemail.com";        $config['smtp_port'] = "465";        $config['smtp_user'] = "maristarchives@gmail.com";        $config['smtp_pass'] = "20MaristArchives15";        $config['charset'] = "utf-8";        $config['mailtype'] = "html";        $config['newline'] = "\r\n";        $this->email->initialize($config);        $this->email->from('maristarchives@gmail.com', ' Marist Archives');        $this->email->to($email_id);        // $this ->email ->bcc("monish.singh1@marist.edu");        $this->email->subject('Honors Thesis Paper');        $emailBody = '<html><body>';        $emailBody .= '<table width="100%"; rules="all" style="border:1px solid #3A5896;" cellpadding="10">';        $emailBody .= "<tr ><td align='center'><img src='https://s.graphiq.com/sites/default/files/10/media/images/Marist_College_2_220374.jpg'  /><h3>James A. Cannavino Library, Marist College</h3> <br/><h3>Honor's Thesis Repository</h3> ";        $emailBody .= "<br/><br/> <h4>Dear $name,<br/><br/> Your Paper uploaded to our repository successfully. You can access the uploaded paper using below link. </h4><br/